              </span></p>
    </div>
    </div><script type="text/javascript">
    var loginname=$('#loginname');
    var code=$('#code');
    var codeimg=$('#codeimg');
    var pcid=$('#pcid');
    var btn=$('#btn');
    var errortip=$('#errortip');
    var tipspan=$('#tip_span');
    var codemsg=$('#codemsg');
    var codep=$('#codep');
    var licon=$('#licon');
    var entry = "weibo";
    $('#change_code').on('click', function () {
        $('#codespan').attr('class','hid');
        changecode();
    });
    loginname.on('focus',function(){
        $('#tip_span').attr('class','hid');
    });
    code.on('focus',function(){
        $('#codespan').attr('class','hid');
    });
    $('#btn').on('click', function () {
        if(!loginname.val()){
            errortip.html('请输入登录名');
            $('#tip_span').attr('class','form_prompt');
        }
        if(!code.val()){
            codemsg.html('请输入验证码');
            codep.attr('class','W_icon icon_rederrorS');
            $('#codespan').attr('class','form_prompt');
        }
        $.post('/iforgot/ajloginname',
                { loginname: loginname.val(),pcid: pcid.val(),pincode:code.val(),entry:entry},
                function(data) {
                    var json = JSON.parse(data);
                    if(json.retcode == 100000){
                        location.href= json.data.url;
                    }else if(json.retcode == 100038){
                        errortip.html('用户名不存在');
                        $('#tip_span').attr('class','form_prompt');
                    } else if(json.retcode == 100037){
                        codemsg.html('请输入正确的验证码');
                        codep.attr('class','W_icon icon_rederrorS');
                        $('#codespan').attr('class','form_prompt');
                        changecode();
                    }else {
                        errortip.html(json.msg);
                        $('#tip_span').attr('class','form_prompt');
                    }


                });
    });
    function changecode(){
        $.post('/iforgot/ajcodeimage','',
                function(data) {
                    var json = JSON.parse(data);
                    if(json.retcode == 100000){
                        codeimg.attr("src",json.data.image);
                        pcid.val(json.data.pcid);
                    }
                });
    }
</script></body></html>